	 private static String imgPath(int type){
		 String path ="";
		 String time = Util.formatDate(new Date(), "yyyyMMdd");
		 	switch (type) {
		 		case 0:
		 			path = ConstantVar.IMG_PATH_TYPE_ZERO;
		 			fileDir(ConstantVar.IMG_PATH+"/"+ConstantVar.IMG_PATH_TYPE_ZERO);
		 			fileDir(ConstantVar.IMG_PATH+"/"+ConstantVar.IMG_PATH_TYPE_ZERO+"/"+time);
		 			
	 				break;
		 		case 1:
		 			path = ConstantVar.IMG_PATH_TYPE_ONE;
		 			fileDir(ConstantVar.IMG_PATH_TYPE_ONE);
		 			fileDir(ConstantVar.IMG_PATH+"/"+ConstantVar.IMG_PATH_TYPE_ONE+"/"+time);
		 			break;
		 		case 2:
		 			path = ConstantVar.IMG_PATH_TYPE_TWO;
		 			fileDir(ConstantVar.IMG_PATH_TYPE_TWO);
		 			fileDir(ConstantVar.IMG_PATH+"/"+ConstantVar.IMG_PATH_TYPE_TWO+"/"+time);
		 			break;
		 		case 3:
		 			path = ConstantVar.IMG_PATH_TYPE_THREE;
		 			fileDir(ConstantVar.IMG_PATH_TYPE_THREE);
		 			fileDir(ConstantVar.IMG_PATH+"/"+ConstantVar.IMG_PATH_TYPE_THREE+"/"+time);
		 			break;
		 		case 4:
		 			path = ConstantVar.IMG_PATH_TYPE_FOUR;
		 			fileDir(ConstantVar.IMG_PATH_TYPE_FOUR);
		 			fileDir(ConstantVar.IMG_PATH+"/"+ConstantVar.IMG_PATH_TYPE_FOUR+"/"+time);
		 			break;
		 		default:
		 			path = ConstantVar.IMG_PATH_TYPE_ZERO;
		 			fileDir(ConstantVar.IMG_PATH_TYPE_ZERO);
		 			fileDir(ConstantVar.IMG_PATH+"/"+ConstantVar.IMG_PATH_TYPE_ZERO+"/"+time);
		 			break;
		 	}
		 return path+"/"+time;
	 }
	 
	 private synchronized static void fileDir(String path){
		 File dir = new File(path);
		 if (!dir.exists()) {
			 try {
				 dir.mkdirs();
			} catch (Exception e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
		 }
	 }
